<?php
//***************************Begin ajax parsing function*************************
if(isset($_POST['action']))
{
	if($_POST['action']=='ContactFrm')
	{
		//check if fields are set;
		if(!isset($_POST['name'])){
			return_error(gettext("Please provide a name"),"name");
		}
		elseif(trim($_POST['name'])=='')
		{
			return_error(gettext("Please provide a name"), "name");
		}
		if(!isset($_POST['email'])){
			return_error(gettext("Please provide an email"),"email");
		}
		elseif(trim($_POST['email'])=='')
		{
			return_error(gettext("Please provide an email"),"email");
		}
		elseif (!is_valid_email($_POST['email'])) {
			return_error(gettext("Email provided is invalid"), "email");			
		}
		if(!isset($_POST['subject'])){
			return_error(gettext("Please provide a subject"),"subject");
		}
		elseif(trim($_POST['subject'])=='')
		{
			return_error(gettext("Please provide a subject"), "subject");
		}
		if(!isset($_POST['message'])){
			return_error(gettext("Please provide a message"),"message");
		}
		elseif(trim($_POST['message'])=='')
		{
			return_error(gettext("Please provide a message"), "message");
		}

	}
}
function return_error($errmsg,$source)
{
	echo json_encode(array("error"=>"true","errormsg"=>$errmsg,"source"=>$source));
	exit();
}
